	$topic_url = generate_board_url() . '/viewtopic.' . $phpEx . '?f=' . $forum_id . '&amp;t=' . $topic_id;
	//$hashtag = htmlspecialchars(urlencode('#'. $config['tweet_hash']));
	$hashtag = '%23'. $config['tweet_hash'];
}
else
{
	$s_tweet = false;
}
//-- fin : Tweet post

edit.find:
// Make sure the tweet is no more than 140 chars.
if ($s_tweet)
{
	$strlen = strlen(htmlspecialchars(urlencode($topic_data['topic_title'] . ': ')) . $topic_url . ' ' . $hashtag);
	if ($strlen > 140)
	{
		$chars = 140 - strlen(': ' . $topic_url . ' ' . $hashtag);
		$tweet = truncate_string($topic_data['topic_title'], $chars, 255, true, '');
		$tweet .= ': ' . $topic_url . ' ' . $hashtag;
	}
	else
	{
		$tweet = htmlspecialchars(urlencode($topic_data['topic_title'] . ' ')) . $topic_url . ' ' . $hashtag;
	}
}

edit.action:
//-- mod : Tweet topic
//-- add
// Make sure the tweet is no more than 140 chars (changed to 121 due to twitters short url thingy).
if ($s_tweet)
{
	$strlen = strlen(htmlspecialchars(urlencode($topic_data['topic_title'])) . ': ' . $hashtag);
	if ($strlen > 121)
	{
		$chars = 121 - $strlen;
		$tweet_text = htmlspecialchars(urlencode(truncate_string($topic_data['topic_title'], $chars, 255, true, '...'))) . ': ' . $hashtag;
	}
	else
	{
		$tweet_text = htmlspecialchars(urlencode($topic_data['topic_title'])) . ': ' . $hashtag;
	}
}
//-- fin : Tweet topic

